package NumbersSums;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 12;
		int num2 = 15;

		System.out.println(num1 & num2); // & operator Bit and
		System.out.println(num1 | num2); // & operator Bit or
		System.out.println(num1 ^ num2); // & operator Bit xor
		System.out.println(num1 >> 2); // >> operator
		System.out.println(num2 << 3); // << operator
		System.out.println(~num2); // ~ operator complementry

	}

}
